import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
export default class AllCourtsPage extends Component {

    state = {
        courts: [],
        newCourt : {
            gymName: '',
            address: '',
            numberOfPlayers: '',
            entryPrice: ''
        }
    }
    componentDidMount() {
        this.getAllCourts()
    }
    getAllCourts = () => {
        axios.get('/api/courts')
            .then((res) => {
                this.setState({courts: res.data})
            })
    }
    handleInputChange = (event) => {
        const copiedCourt = {...this.state.newCourt}
        copiedCourt[event.target.name] = event.target.value

        this.setState({newCourt: copiedCourt})
    }
    render() {
        let courtsList = this.state.courts.map((court) => {
            return (
                <div>
                <Link 
                key={court._id}
                to={`/courts/${court._id}`}
                >
                {court.gymName}
                </Link>
                </div>

            )
        })
        return (
            <div>
                {courtsList}
                <h1>Hello</h1>
            </div>
        )
    }
}
