import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";
import { Link } from "react-router-dom";
import DisplayProfile from "./DisplayProfile";
import SingleCourtPage from "../Court/SingleCourtPage";
export default class SinglePlayerPage extends Component {
  state = {
    players: [],
    reDirectToHome: false
  };
  componentDidMount() {
    axios
      .get(`/api/courts/${this.props.match.params.courtId}/players`)
      .then(res => {
        this.setState({ players: res.data });
      });
  }

  handleDeletePlayer = () => {
    axios.delete(`/players`).then(() => {
      this.setState({ reDirectToHome: true });
    });
  };

  render() {
    let playerArray = this.state.players.map(singlePlayer => {
      return (
        <div>
          <DisplayProfile
            name={singlePlayer.name}
            position={singlePlayer.position}
            ageGroup={singlePlayer.ageGroup}
            bio={singlePlayer.bio}
          />
        </div>
      );
    });
    return (
      <div>
        {playerArray}
        <h1>Hello</h1>
        {/* <h2>{this.state.player.name}</h2>
        <h3>{this.state.player.position}</h3>
        <p>{this.state.player.ageGroup}</p>
        <p>{this.state.player.bio}</p> */}

        <button onClick={this.handleDeletePlayer}>Delete Player</button>
      </div>
    );
  }
}
